      out.write("    const passwordInput = document.getElementById('password');\n");
      out.write("    const emailError = document.getElementById('emailError');\n");
      out.write("    const passwordError = document.getElementById('passwordError');\n");
      out.write("\n");
      out.write("    // Email validation\n");
      out.write("    function validateEmail(email) {\n");
      out.write("        const emailRegex = /^[^\\s@]+@[^\\s@]+\\.[^\\s@]+$/;\n");
      out.write("        return emailRegex.test(email);\n");
      out.write("    }\n");
      out.write("\n");
      out.write("    // Show error message\n");
      out.write("    function showError(element, message) {\n");
      out.write("        element.textContent = message;\n");
      out.write("        element.classList.remove('hidden');\n");
      out.write("        element.previousElementSibling.classList.add('border-red-600');\n");
      out.write("    }\n");
      out.write("\n");
      out.write("    // Hide error message\n");
      out.write("    function hideError(element) {\n");
      out.write("        element.textContent = '';\n");
      out.write("        element.classList.add('hidden');\n");
      out.write("        element.previousElementSibling.classList.remove('border-red-600');\n");
      out.write("    }\n");
      out.write("\n");
      out.write("    // Validate email field\n");
      out.write("    emailInput.addEventListener('blur', function() {\n");
      out.write("        const email = emailInput.value.trim();\n");
      out.write("\n");
      out.write("        if (email === '') {\n");
      out.write("            showError(emailError, 'Vui lòng nhập địa chỉ email');\n");
      out.write("        } else if (!validateEmail(email)) {\n");
      out.write("            showError(emailError, 'Địa chỉ email không hợp lệ');\n");
      out.write("        } else {\n");
      out.write("            hideError(emailError);\n");
      out.write("        }\n");
      out.write("    });\n");
      out.write("\n");
      out.write("    // Validate password field\n");
