      out.write("        } else if (!validateEmail(email)) {\n");
      out.write("            showError(emailError, 'Địa chỉ email không hợp lệ');\n");
      out.write("            isValid = false;\n");
      out.write("        } else {\n");
      out.write("            hideError(emailError);\n");
      out.write("        }\n");
      out.write("\n");
      out.write("        // Validate password\n");
      out.write("        const password = passwordInput.value;\n");
      out.write("        if (password === '') {\n");
      out.write("            showError(passwordError, 'Vui lòng nhập mật khẩu');\n");
      out.write("            isValid = false;\n");
      out.write("        } else if (password.length < 6) {\n");
      out.write("            showError(passwordError, 'Mật khẩu phải có ít nhất 6 ký tự');\n");
      out.write("            isValid = false;\n");
      out.write("        } else {\n");
      out.write("            hideError(passwordError);\n");
      out.write("        }\n");
      out.write("\n");
      out.write("        if (!isValid) {\n");
      out.write("            e.preventDefault();\n");
      out.write("        }\n");
      out.write("    });\n");
      out.write("\n");
      out.write("    // Clear errors on input\n");
